import React, { useState, useCallback, useEffect } from 'react';
import useWebSocket, { ReadyState } from 'react-use-websocket';
import "./App.scss"

const App = () => {
  // State for users and messages
  const [users, setUsers] = useState([]);
  const [messages, setMessages] = useState([]);
  // const [socketUrlUsers] = useState('ws://localhost:8000/users'); // WebSocket URL for users
  // const [socketUrlRoom] = useState('ws://localhost:8000/room/1'); // WebSocket URL for room messages

  // WebSocket for users
  const { lastMessage: lastMessageUsers, readyState: readyStateUsers } = useWebSocket('ws://localhost:8000/users');

  // WebSocket for messages
  // const { sendMessage, lastMessage: lastMessageRoom, readyState: readyStateRoom } = useWebSocket('ws://localhost:8000/room/1');

  // Handle the messages and update state accordingly
  useEffect(() => {
    if (lastMessageUsers?.data) {
      console.log(lastMessageUsers.data)
      const usersList = lastMessageUsers.data.split("\n");

      const fetchUsernames = async () => {
        const newUsers = [];
        for (const user of usersList) {
          let found = false;

          for (let i = 0; i < users.length; i++) {
            if (user === users[i].id) {
              found = true;
              break;
            }
          }

          if (!found) {
            try {
              const response = await fetch("https://usernameapiv1.vercel.app/api/random-usernames");
              const data = await response.json();
              const username = data.usernames[0];

              newUsers.push({ id: user, username: username });
            } catch (error) {
              console.error("Error fetching username:", error);
            }
          }
        }

        setUsers((prevUsers) => [
          ...prevUsers,
          ...newUsers,
        ]);

        setUsers((prevUsers) => {
          const filteredUsers = prevUsers.filter(user => usersList.includes(user.id));
          return filteredUsers;
        });
      };

      fetchUsernames();
    }
  }, [lastMessageUsers]);

  // Handle new messages from the room
  // useEffect(() => {
  //   if (lastMessageRoom?.data) {
  //     const newMessage = lastMessageRoom.data;
  //     setMessages((prevMessages) => [...prevMessages, newMessage]);
  //   }
  // }, [lastMessageRoom]);

  // Send a message to the room
  const handleSendMessage = useCallback(() => {
    sendMessage('Hello from the room!');
  }, [sendMessage]);

  const connectionStatusUsers = {
    [ReadyState.CONNECTING]: 'Connecting',
    [ReadyState.OPEN]: 'Open',
    [ReadyState.CLOSING]: 'Closing',
    [ReadyState.CLOSED]: 'Closed',
    [ReadyState.UNINSTANTIATED]: 'Uninstantiated',
  }[readyStateUsers];

  const connectionStatusRoom = {
    [ReadyState.CONNECTING]: 'Connecting',
    [ReadyState.OPEN]: 'Open',
    [ReadyState.CLOSING]: 'Closing',
    [ReadyState.CLOSED]: 'Closed',
    [ReadyState.UNINSTANTIATED]: 'Uninstantiated',
  }[readyStateRoom];

  return (
    <div>
      <h2>Users ({connectionStatusUsers})</h2>
      <div className='users'>
        {users.map((user) => (
          <div className="user" key={user.id}>
            <svg xmlns="http://www.w3.org/2000/svg" width="64" height="64" fill="currentColor" className="bi bi-person-circle" viewBox="0 0 16 16">
              <path d="M11 6a3 3 0 1 1-6 0 3 3 0 0 1 6 0"/>
              <path fillRule="evenodd" d="M0 8a8 8 0 1 1 16 0A8 8 0 0 1 0 8m8-7a7 7 0 0 0-5.468 11.37C3.242 11.226 4.805 10 8 10s4.757 1.225 5.468 2.37A7 7 0 0 0 8 1"/>
            </svg>
            <p>{user.username}</p>
          </div>
        ))}
      </div>

      <h2>Room Messages ({connectionStatusRoom})</h2>
      <div className="messages">
        {messages.map((message, index) => (
          <div key={index} className="message">
            <p>{message}</p>
          </div>
        ))}
      </div>

      <button onClick={handleSendMessage} disabled={readyStateRoom !== ReadyState.OPEN}>
        Send 'Hello from the room'
      </button>
    </div>
  );
};

export default App;
