import React, { useState, useEffect, useRef } from 'react';

const Home = () => {
  const [connected, setConnected] = useState(false);
  const [messages, setMessages] = useState([]);
  const socketRef = useRef(null);  // Using useRef to hold the WebSocket instance

  // Connect to WebSocket
  const connectWebSocket = () => {
    // Check if the WebSocket connection is already established
    if (socketRef.current) {
      console.log("WebSocket is already connected.");
      return;
    }

    const ws = new WebSocket('ws://localhost:800');  // Update to server's IP for remote devices

    ws.onopen = () => {
      setConnected(true);  // Set connected state to true
      console.log('WebSocket connected');
    };

    ws.onmessage = (event) => {
      console.log('Message from server:', event.data);
      setMessages((prevMessages) => [...prevMessages, event.data]);  // Add new message to the list
    };

    ws.onclose = () => {
      setConnected(false);
      console.log('WebSocket disconnected');
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    socketRef.current = ws;  // Store the WebSocket instance in a ref
  };

  // Send message to WebSocket
  const sendMessage = () => {
    if (socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
      socketRef.current.send('Hello from React!');
    } else {
      console.log("WebSocket is not open");
    }
  };

  // Cleanup WebSocket connection when component unmounts
  useEffect(() => {
    connectWebSocket();  // Initiate WebSocket connection when the component mounts

    return () => {
      // Close WebSocket connection when the component unmounts
      if (socketRef.current) {
        socketRef.current.close();
      }
    };
  }, []);  // The empty dependency array ensures this runs only once on mount

  return (
    <div>
      <h1>WebSocket Client</h1>
      <button onClick={sendMessage} disabled={!connected}>
        Send Message
      </button>
      <div>
        <h2>Messages:</h2>
        <ul>
          {messages.map((message, index) => (
            <li key={index}>{message}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Home;
