import React, { useState, useEffect, useRef } from 'react';

const Home = () => {
  const [connected, setConnected] = useState(false);
  const [messages, setMessages] = useState([]);
  const socketRef = useRef(null); // Using useRef to store the WebSocket instance

  // Connect to WebSocket server
  const connectWebSocket = () => {
    const ws = new WebSocket('ws://localhost:800');  // Connect to the Go WebSocket server

    ws.onopen = () => {
      setConnected(true);  // Set the connection status when WebSocket is open
      console.log('WebSocket connected');
    };

    ws.onmessage = (event) => {
      console.log('Message from server:', event.data);
      // Append the message to the list of messages
      setMessages((prevMessages) => [...prevMessages, event.data]);
    };

    ws.onclose = () => {
      setConnected(false);  // Update the connection status when WebSocket is closed
      console.log('WebSocket disconnected');
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    socketRef.current = ws;  // Save the WebSocket instance to a ref
  };

  // Send message to the WebSocket server
  const sendMessage = () => {
    if (socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
      const message = "Hello from React!";
      socketRef.current.send(message);  // Send the message to the server
    } else {
      console.log("WebSocket is not open.");
    }
  };

  // Connect WebSocket on component mount and clean up on unmount
  useEffect(() => {
    connectWebSocket();  // Establish WebSocket connection

    return () => {
      // Close WebSocket connection when the component is unmounted
      if (socketRef.current) {
        socketRef.current.close();
      }
    };
  }, []);

  return (
    <div>
      <h1>WebSocket Client</h1>
      <button onClick={sendMessage} disabled={!connected}>
        Send Message
      </button>
      <div>
        <h2>Messages:</h2>
        <ul>
          {messages.map((message, index) => (
            <li key={index}>{message}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Home;
