import React, { useState, useCallback, useEffect } from 'react';
import useWebSocket, { ReadyState } from 'react-use-websocket';
import "./App.scss"
import Modal from '../../components/modal/Modal';

const App = () => {
  // State for users and messages
  const [users, setUsers] = useState([]);
  const [currentUser, setCurrentUser] = useState({ id: "", username: "" });
  const [messages, setMessages] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [roomId, setRoomId] = useState(null); // State to hold the room ID
  const [receivedFiles, setReceivedFiles] = useState([])

  // WebSocket for users
  const { lastMessage: lastMessageUsers, readyState: readyStateUsers } = useWebSocket('ws://wild-trams-melt.loca.lt/users');

  // WebSocket for room, using roomId state
  const { sendMessage, lastMessage: lastMessageRoom, readyState: readyStateRoom } = useWebSocket(roomId ? `ws://wild-trams-melt.loca.lt/room/${roomId}` : null);

  const handleFileChange = (e, userId, users) => {
    console.log(users)
    const selectedFile = e.target.files[0];
    handleSendFile(selectedFile, userId)
  };

  const formatName = (id) => {
    let splittedId = id.split(":");
    let prefNum = Number(splittedId[splittedId.length - 1].slice(0, (splittedId.length / 2) + 1));
    let sufNum = Number(splittedId[splittedId.length - 1].slice(splittedId.length / 2 + 1));

    return prefixes[prefNum % 50] + " " + suffixes[sufNum % 50]
  }

  const handleDownload = (file) => {
    const { fileName, fileData } = file;

    // Create a link element
    const link = document.createElement('a');
    link.href = fileData; // Set the href to the file data
    link.download = fileName; // Set the download attribute with the file name

    // Append the link to the body (required for Firefox)
    document.body.appendChild(link);

    // Trigger the download
    link.click();

    // Clean up and remove the link
    document.body.removeChild(link);

    setReceivedFiles(files => files.slice(1))
  };

  const handleSendFile = (file, userId) => {
    console.log(currentUser)
    console.log(userId)
    if (file && userId) {

      // console.log(currentUser)
      // console.log(userId)
      const reader = new FileReader();
      reader.onload = () => {
        const fileData = reader.result;

        // Send the file over the WebSocket to the recipient's room
        const fileMessage = {
          type: 'file',
          fileName: file.name,
          fileData: fileData,
          recipient: userId,
          sender: currentUser.id
        };

        // Construct the WebSocket URL for the recipient's room
        const recipientRoomUrl = `ws://wild-trams-melt.loca.lt/room/${userId}`;

        // Create a new WebSocket connection to the recipient's room
        const recipientSocket = new WebSocket(recipientRoomUrl);

        recipientSocket.onopen = () => {
          // Send the file data to the backend
          recipientSocket.send(JSON.stringify(fileMessage));
          recipientSocket.close(); // Close the socket after sending
        };

        recipientSocket.onerror = (error) => {
          console.error("WebSocket error:", error);
        };
      };

      // Convert file to base64
      reader.readAsDataURL(file);
    }
  };

  // Handle the messages and update state accordingly for users
  useEffect(() => {
    if (lastMessageUsers?.data) {

      // Parse the JSON data
      const userData = JSON.parse(lastMessageUsers.data);

      // Extract the current user ID and the list of users
      const currentUserId = userData.current_user;
      const usersList = userData.users;

      // Update the users state with the new users
      setUsers(usersList.map(id => {

        return {
          id,
          username: formatName(id)
        };
      }));

      if (currentUser.id === "") {
        setCurrentUser({
          id: currentUserId,
          username: formatName(currentUserId)
        });
        setRoomId(currentUserId);
      }

      setUsers((prevUsers) => {
        const filteredUsers = prevUsers.filter(user => usersList.includes(user.id));
        return filteredUsers;
      });
    }
  }, [lastMessageUsers]);


  useEffect(() => {
    if (lastMessageRoom?.data) {
      const newFile = lastMessageRoom.data;
      setReceivedFiles((prevFiles) => [...prevFiles, newFile]);
    }
  }, [lastMessageRoom]);

  const connectionStatusUsers = {
    [ReadyState.CONNECTING]: 'Connecting',
    [ReadyState.OPEN]: 'Open',
    [ReadyState.CLOSING]: 'Closing',
    [ReadyState.CLOSED]: 'Closed',
    [ReadyState.UNINSTANTIATED]: 'Uninstantiated',
  }[readyStateUsers];

  const connectionStatusRoom = {
    [ReadyState.CONNECTING]: 'Connecting',
    [ReadyState.OPEN]: 'Open',
    [ReadyState.CLOSING]: 'Closing',
    [ReadyState.CLOSED]: 'Closed',
    [ReadyState.UNINSTANTIATED]: 'Uninstantiated',
  }[readyStateRoom];

  return (
    <div className='home'>
      { receivedFiles.length>0 &&
        <Modal sender={formatName(JSON.parse(receivedFiles[0]).sender)} filename={JSON.parse(receivedFiles[0]).fileName} onSave={() => handleDownload(JSON.parse(receivedFiles[0]))} onCancel={() => {setReceivedFiles(files => files.slice(1))}} />
      }

      <div className='title'>
        <span className="header">Drop 'n Share</span>
        {(readyStateRoom === -1 && readyStateUsers === -1) &&
          <svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" fill="red" className="bi bi-x-circle-fill" viewBox="0 0 16 16">
            <path d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0M5.354 4.646a.5.5 0 1 0-.708.708L7.293 8l-2.647 2.646a.5.5 0 0 0 .708.708L8 8.707l2.646 2.647a.5.5 0 0 0 .708-.708L8.707 8l2.647-2.646a.5.5 0 0 0-.708-.708L8 7.293z" />
          </svg>
        }
        {(readyStateRoom !== 1 && readyStateUsers !== 1) &&
          <svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" fill="yellow" className="bi bi-dash-circle-fill" viewBox="0 0 16 16">
            <path d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0M4.5 7.5a.5.5 0 0 0 0 1h7a.5.5 0 0 0 0-1z" />
          </svg>
        }
        {(readyStateRoom === 1 && readyStateUsers === 1) &&
          <svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" fill="green" className="bi bi-check-circle-fill" viewBox="0 0 16 16">
            <path d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0m-3.97-3.03a.75.75 0 0 0-1.08.022L7.477 9.417 5.384 7.323a.75.75 0 0 0-1.06 1.06L6.97 11.03a.75.75 0 0 0 1.079-.02l3.992-4.99a.75.75 0 0 0-.01-1.05z" />
          </svg>
        }
        <h2>You are:</h2>
        <h1>{currentUser.username}</h1>
      </div>

      <div className='users'>
        {users.map((user) => (
          <div className="user" key={user.id}>
            <label htmlFor="file-input">
              <svg xmlns="http://www.w3.org/2000/svg" width="64" height="64" fill="currentColor" className="bi bi-person-circle" viewBox="0 0 16 16">
                <path d="M11 6a3 3 0 1 1-6 0 3 3 0 0 1 6 0" />
                <path fillRule="evenodd" d="M0 8a8 8 0 1 1 16 0A8 8 0 0 1 0 8m8-7a7 7 0 0 0-5.468 11.37C3.242 11.226 4.805 10 8 10s4.757 1.225 5.468 2.37A7 7 0 0 0 8 1" />
              </svg>
            </label>
            <input type="file" onChange={(e) => handleFileChange(e, user.id, users)} value={file} id="file-input" hidden />
            <p>{user.username}</p>
          </div>
        ))}
      </div>
    </div>
  );
};


const prefixes = [
