import React, { useState, useEffect, useRef } from 'react';

const Home = () => {
  const [connected, setConnected] = useState(false);
  const [messages, setMessages] = useState([]);
  const socketRef = useRef(null);  // Using useRef to hold the WebSocket instance

  // Function to create a WebSocket connection
  const connectWebSocket = () => {
    const ws = new WebSocket('ws://localhost:800');  // Use server's IP if needed (e.g., ws://192.168.x.x:800)

    ws.onopen = () => {
      setConnected(true);  // Set connected to true when the connection opens
      console.log('WebSocket connected');
    };

    ws.onmessage = (event) => {
      console.log('Message from server:', event.data);
      setMessages((prevMessages) => [...prevMessages, event.data]);  // Add new message to state
    };

    ws.onclose = () => {
      setConnected(false);  // Set connected to false when the WebSocket closes
      console.log('WebSocket disconnected');
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    socketRef.current = ws;  // Store the WebSocket instance in state
  };

  // Function to send a message to the WebSocket server
  const sendMessage = () => {
    if (socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
      socketRef.current.send('Hello from React!');
    } else {
      console.log("WebSocket is not open");
    }
  };

  // useEffect hook to establish the WebSocket connection once when the component mounts
  useEffect(() => {
    connectWebSocket();  // Create WebSocket connection when component mounts

    return () => {
      // Cleanup WebSocket connection when component unmounts
      if (socketRef.current) {
        socketRef.current.close();
      }
    };
  }, []);  // Empty dependency array means this will only run once when the component mounts

  return (
    <div>
      <h1>WebSocket Client</h1>
      <button onClick={sendMessage} disabled={!connected}>
        Send Message
      </button>
      <div>
        <h2>Messages:</h2>
        <ul>
          {messages.map((message, index) => (
            <li key={index}>{message}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Home;
