import React, { useState, useCallback, useEffect } from 'react';
import useWebSocket, { ReadyState } from 'react-use-websocket';

const App = () => {
  //Public API that will echo messages sent to it back to the client
  const [socketUrl, setSocketUrl] = useState('ws://localhost:8000');
  const [users, setUsers] = useState([])

  const { sendMessage, lastMessage, readyState } = useWebSocket(socketUrl);

  useEffect(() => {
    if (lastMessage?.data) {
      let usersList = lastMessage.data.split("\n");

      const fetchUsernames = async () => {
        const newUsers = [];
        for (const user of usersList) {
          let found = false;

          for (let i = 0; i < users.length; i++) {
            if (user === users[i].id) {
              found = true;
              break;
            }
          }

          if (!found) {
            try {
              const response = await fetch("https://usernameapiv1.vercel.app/api/random-usernames");
              const data = await response.json();
              const username = data.usernames[0];

              newUsers.push({ id: user, username: username });
            } catch (error) {
              console.error("Error fetching username:", error);
            }
          }
        }

        // After all users have been processed, update state
        setUsers((prevUsers) => [
          ...prevUsers,
          ...newUsers,
        ]);

        // Now filter out any users whose 'id' doesn't exist in the 'usersList'
        setUsers((prevUsers) => {
          const filteredUsers = prevUsers.filter(user => usersList.includes(user.id));
          return filteredUsers;
        });
      };

      fetchUsernames();
    }
  }, [lastMessage]);



  useEffect(() => {
    console.log(users)
  }, [users])

  const handleClickSendMessage = useCallback(() => sendMessage('Hello'), []);

  const connectionStatus = {
    [ReadyState.CONNECTING]: 'Connecting',
    [ReadyState.OPEN]: 'Open',
    [ReadyState.CLOSING]: 'Closing',
    [ReadyState.CLOSED]: 'Closed',
    [ReadyState.UNINSTANTIATED]: 'Uninstantiated',
  }[readyState];

  return (
    <div>
      {/* <button
        onClick={handleClickSendMessage}
        disabled={readyState !== ReadyState.OPEN}
      >
        Click Me to send 'Hello'
      </button> */}
      {/* {lastMessage ? <span>Last message: {lastMessage.data}</span> : null} */}
      {users.map(() => (
        <>
          <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-person-circle" viewBox="0 0 16 16">
            <path d="M11 6a3 3 0 1 1-6 0 3 3 0 0 1 6 0"/>
            <path fill-rule="evenodd" d="M0 8a8 8 0 1 1 16 0A8 8 0 0 1 0 8m8-7a7 7 0 0 0-5.468 11.37C3.242 11.226 4.805 10 8 10s4.757 1.225 5.468 2.37A7 7 0 0 0 8 1"/>
          </svg>
        </>
      ))}
      <ul>
      </ul>
    </div>
  );
};

export default App
