import React, { useState, useEffect, useRef } from 'react';

const Home = () => {
  const [connected, setConnected] = useState(false);
  const [messages, setMessages] = useState([]);
  const socketRef = useRef(null);  // Using useRef to store WebSocket instance

  // Function to create a WebSocket connection
  const connectWebSocket = () => {
    // If WebSocket already exists, avoid creating a new one
    if (socketRef.current) return;

    const ws = new WebSocket('ws://localhost:800');  // Use server's IP if needed (e.g., ws://192.168.x.x:800)

    ws.onopen = () => {
      setConnected(true);
      console.log('WebSocket connected');
    };

    ws.onmessage = (event) => {
      console.log('Message from server:', event.data);
      setMessages((prevMessages) => [...prevMessages, event.data]);  // Append new message to messages state
    };

    ws.onclose = () => {
      setConnected(false);
      console.log('WebSocket disconnected');
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    socketRef.current = ws;  // Store WebSocket instance in ref
  };

  // Function to send a message to the WebSocket server
  const sendMessage = () => {
    if (socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
      socketRef.current.send('Hello from React!');
    } else {
      console.log("WebSocket is not open");
    }
  };

  // Set up WebSocket connection once when component mounts
  useEffect(() => {
    connectWebSocket();

    // return () => {
    //   // Clean up WebSocket connection on component unmount
    //   if (socketRef.current) {
    //     socketRef.current.close();
    //   }
    // };
  }, []);  // Empty dependency array ensures this runs only once on mount

  return (
    <div>
      <h1>WebSocket Client</h1>
      <button onClick={sendMessage} disabled={!connected}>
        Send Message
      </button>
      <div>
        <h2>Messages:</h2>
        <ul>
          {messages.map((message, index) => (
            <li key={index}>{message}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Home;
