import React, { useState, useCallback, useEffect } from 'react';
import useWebSocket, { ReadyState } from 'react-use-websocket';

const App = () => {
  //Public API that will echo messages sent to it back to the client
  const [socketUrl, setSocketUrl] = useState('ws://localhost:8000');
  const [users, setUsers] = useState([])

  const { sendMessage, lastMessage, readyState } = useWebSocket(socketUrl);

  useEffect(() => {
    if (lastMessage && Array.isArray(lastMessage.data)) {
      lastMessage.data.forEach(async (user) => {
        let found = false;
        // Loop through existing users to check if the user is already added
        for (let i = 0; i < users.length; i++) {
          if (user === users[i].id) {
            found = true;
            break;
          }
        }

        if (!found) {
          try {
            const response = await fetch("https://usernameapiv1.vercel.app/api/random-usernames");
            const data = await response.json();
            const username = data.usernames[0];

            setUsers((prevUsers) => [
              ...prevUsers,
              { id: user, username: username }
            ]);
          } catch (error) {
            console.error("Error fetching username:", error);
          }
        }
      });
    }

    console.log(users);
  }, [lastMessage, users]);

  const handleClickSendMessage = useCallback(() => sendMessage('Hello'), []);

  const connectionStatus = {
    [ReadyState.CONNECTING]: 'Connecting',
    [ReadyState.OPEN]: 'Open',
    [ReadyState.CLOSING]: 'Closing',
    [ReadyState.CLOSED]: 'Closed',
    [ReadyState.UNINSTANTIATED]: 'Uninstantiated',
  }[readyState];

  return (
    <div>
      <button
        onClick={handleClickSendMessage}
        disabled={readyState !== ReadyState.OPEN}
      >
        Click Me to send 'Hello'
      </button>
      <span>The WebSocket is currently {connectionStatus}</span>
      {lastMessage ? <span>Last message: {lastMessage.data}</span> : null}
      <ul>
      </ul>
    </div>
  );
};

export default App
