import React, { useState, useEffect } from 'react';

const Home = () => {
  const [connected, setConnected] = useState(false);
  const [messages, setMessages] = useState([]);
  const [socket, setSocket] = useState(null);

  // Connect to WebSocket
  const connectWebSocket = () => {
    const ws = new WebSocket('ws://localhost:800');  // WebSocket URL

    ws.onopen = () => {
      setConnected(true);  // Mark as connected
      console.log('WebSocket connected');
    };

    // Handle incoming messages from WebSocket
    ws.onmessage = (event) => {
      console.log('Message from server:', event.data);
      setMessages((prevMessages) => [...prevMessages, event.data]); // Update state with new message
    };

    // Handle WebSocket close event
    ws.onclose = () => {
      setConnected(false);  // Mark as disconnected
      console.log('WebSocket disconnected');
    };

    // Handle WebSocket errors
    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    setSocket(ws);  // Store the socket connection
  };

  // Send message to WebSocket
  const sendMessage = () => {
    if (socket && socket.readyState === WebSocket.OPEN) {
      socket.send('Hello from React!');
    } else {
      console.log("WebSocket is not open");
    }
  };

  // Cleanup WebSocket connection when component unmounts
  useEffect(() => {
    connectWebSocket();  // Initiate WebSocket connection when the component mounts

    return () => {
      // Close WebSocket connection when the component unmounts
      if (socket) {
        socket.close();
      }
    };
  }, []);

  return (
    <div>
      <h1>WebSocket Client</h1>
      <button onClick={sendMessage} disabled={!connected}>
        Send Message
      </button>
      <div>
        <h2>Messages:</h2>
        <ul>
          {messages.map((message, index) => (
            <li key={index}>{message}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Home;
